// Classify input type and extract content
const items = $input.all();
const results = [];

// Helper: Normalize account name to slug format
function normalizeToSlug(text) {
  if (!text) return null;
  return text
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')  // Replace non-alphanumeric with dashes
    .replace(/^-+|-+$/g, '')      // Trim leading/trailing dashes
    .replace(/-+/g, '-');          // Collapse multiple dashes
}

for (const item of items) {
  // Check if input is from webhook (MCP) or Gmail
  const isWebhook = item.json.body !== undefined;

  let content, subject, input_type, deal_slug, salesforce_opportunity_id, date, source_filename;
  let opportunity_name, account_name, close_date, trigger_type;

  if (isWebhook) {
    // MCP/Webhook input - convert camelCase to snake_case
    const body = item.json.body;
    // Generate content from webhook fields if not provided
    if (body.content) {
      content = body.content;
    } else {
      // Build descriptive content from available fields
      const parts = [];
      if (trigger_type && trigger_type !== 'deal_intake') {
        parts.push(`${trigger_type}`);
      } else {
        parts.push('New deal opportunity');
      }

      if (opportunity_name) {
        parts.push(`"${opportunity_name}"`);
      }

      if (account_name) {
        parts.push(`at ${account_name}`);
      }

      if (close_date) {
        parts.push(`(expected close: ${close_date})`);
      }

      content = parts.join(' ') + '. This is initial deal intake with minimal information.';
    }
    input_type = body.input_type || body.inputType || 'call_transcript';
    opportunity_name = body.opportunity_name || body.opportunityName || null;
    account_name = body.account_name || body.accountName || null;
    close_date = body.close_date || body.closeDate || null;
    trigger_type = body.trigger_type || body.triggerType || 'deal_intake';
    salesforce_opportunity_id = body.salesforce_opportunity_id || body.salesforceOpportunityId || null;
    date = body.date || new Date().toISOString().split('T')[0];
    source_filename = body.source_filename || body.sourceFilename || 'mcp-input';

    // Slug generation logic:
    // 1. Use provided slug if exists
    // 2. Normalize account_name if provided
    // 3. Fall back to date-based slug
    if (body.deal_slug || body.dealSlug) {
      deal_slug = body.deal_slug || body.dealSlug;
    } else if (account_name) {
      const accountSlug = normalizeToSlug(account_name);
      deal_slug = `${accountSlug}-${date}`;
    } else {
      deal_slug = `deal-${date}`;
    }

  } else {
    // Gmail input
    subject = item.json.subject || '';
    const text = item.json.text || item.json.textPlain || '';
    content = text;

    // Check for X-ECHO-Deal-Slug header first (most reliable)
    const headers = item.json.headers || {};
    const echoSlugHeader = headers['x-echo-deal-slug'] || headers['X-ECHO-Deal-Slug'] || headers['X-Echo-Deal-Slug'];

    if (echoSlugHeader) {
      deal_slug = echoSlugHeader;
    } else {
      // Strip email prefixes (Re:, Fwd:, FW:, etc.) then extract account name
      const cleanSubject = subject.replace(/^(Re:|Fwd:|FW:|RE:|FWD:)\s*/gi, '').trim();
      const subjectMatch = cleanSubject.match(/^([^-]+)/);
      if (subjectMatch) {
        const extractedAccount = subjectMatch[1].trim();
        account_name = extractedAccount;
        deal_slug = `${normalizeToSlug(extractedAccount)}-${new Date().toISOString().split('T')[0]}`;
      } else {
        deal_slug = `deal-${new Date().toISOString().split('T')[0]}`;
      }
    }

    // Determine input type
    input_type = 'email';
    if (subject.toLowerCase().includes('transcript')) {
      input_type = 'call_transcript';
    } else if (subject.toLowerCase().includes('notes')) {
      input_type = 'call_notes';
    }

    salesforce_opportunity_id = null;
    date = new Date().toISOString().split('T')[0];
    source_filename = `email-${item.json.id || 'unknown'}`;
    opportunity_name = null;
    close_date = null;
    trigger_type = 'email_trigger';
  }

  results.push({
    json: {
      content: content,
      subject: subject,
      input_type: input_type,
      deal_slug: deal_slug,
      salesforce_opportunity_id: salesforce_opportunity_id,
      date: date,
      source_filename: source_filename,
      opportunity_name: opportunity_name,
      account_name: account_name,
      close_date: close_date,
      trigger_type: trigger_type
    }
  });
}

return results;

const items = $input.all();

// Get input data using correct n8n syntax
const inputData = $('Input Classifier').item.json;

// Check what we got from Load Deal Memory
const dealData = items?.[0]?.json;
const idValue = dealData?.id;

// Check for valid deal - id must be a positive number
const hasValidDeal = typeof idValue === 'number' && idValue > 0;

if (!hasValidDeal) {
  // No valid deal
  return [{ 
    json: { 
      _noDealsFound: true,
      _contextSource: 'new_opportunity',
      deal_slug: inputData.deal_slug,
      opportunity_name: inputData.opportunity_name,
      account_name: inputData.account_name,
      close_date: inputData.close_date,
      salesforce_opportunity_id: inputData.salesforce_opportunity_id,
      trigger_type: inputData.trigger_type,
      content: inputData.content,
      input_type: inputData.input_type
    }
  }];
}

// Deal found
return [{ 
  json: {
    ...dealData,
    _noDealsFound: false,
    _contextSource: 'existing_deal',
    content: inputData.content
  }
}];

// Format outputs from ECHO Engine for PostgreSQL and review portal
const engine = $('ECHO Engine').item.json;
const input = $('Input Classifier').item.json;
const dealContext = $('Merge Deal Context').item.json;

if (!engine || !input) {
  throw new Error('Missing required data from previous nodes');
}

function esc(val) {
  if (val === null || val === undefined || val === '') return 'NULL';
  return "'" + String(val).replace(/'/g, "''") + "'";
}
function escStr(val) {
  if (val === null || val === undefined) return 'NULL';
  return "'" + String(val).replace(/'/g, "''") + "'";
}
function escNum(val) {
  const n = Number(val);
  return isNaN(n) ? '0' : String(n);
}

function unwrap(node) {
  const raw = node.output !== undefined ? node.output : node;
  if (typeof raw === 'string') {
    try {
      const cleaned = raw.replace(/^```json\s*/i, '').replace(/^```\s*/i, '').replace(/```\s*$/i, '').trim();
      return JSON.parse(cleaned);
    } catch(e) { return {}; }
  }
  return raw;
}

const data = unwrap(engine);
const sf = data.salesforce || {};
const narr = data.narratives || {};

function extractStageCode(casStr) {
  if (!casStr) return '1A';
  const match = casStr.match(/^([1-5][AB])/i);
  if (match) return match[1].toUpperCase();
  const s = casStr.toLowerCase();
  if (s.includes('close') || s.includes('won') || s.includes('lost')) return '5A';
  if (s.includes('negot') || s.includes('contract') || s.includes('msa') || s.includes('sow')) return '4A';
  if (s.includes('prov') || s.includes('valid') || s.includes('proposal') || s.includes('demo')) return '3A';
  if (s.includes('qualif') || s.includes('engaged') || s.includes('discover')) return '2A';
  return '1A';
}

const deal_slug = input.deal_slug || '';
const salesforce_opportunity_id = dealContext?.salesforce_opportunity_id || input.salesforce_opportunity_id || null;
const opportunity_name = input.opportunity_name || dealContext?.opportunity_name || '';
const account_name = input.account_name || dealContext?.account_name || '';

const close_date = sf.close_date || null;
const stage = sf.stage || '';
const cas = sf.cas || '';
const forecast_category = sf.forecast_category || '';
const salesforce_block_1_dap = sf.salesforce_block_1_dap || '';
const risks = sf.risks || '';
const next_step = sf.next_step || '';

const pain_score = Number(sf.pain_score) || 0;
const pain_evidence = sf.pain_evidence || '';
const power_score = Number(sf.power_score) || 0;
const power_evidence = sf.power_evidence || '';
const vision_score = Number(sf.vision_score) || 0;
const vision_evidence = sf.vision_evidence || '';
const value_score = Number(sf.value_score) || 0;
const value_evidence = sf.value_evidence || '';
const change_score = Number(sf.change_score) || 0;
const change_evidence = sf.change_evidence || '';
const control_score = Number(sf.control_score) || 0;
const control_evidence = sf.control_evidence || '';
const p2v2c2_total = pain_score + power_score + vision_score + value_score + change_score + control_score;

const call_summary = narr.call_summary || '';
const highlights = narr.highlights || '';
const action_items = narr.action_items || '';
const p2v2c2_scoring = narr.p2v2c2_scoring || '';
const internal_actions = narr.internal_actions || '';
const services_narrative = narr.services_narrative || '';
const general_narrative = narr.general_narrative || '';
const anecdotes = narr.anecdotes || '';
const internal_team_update = narr.internal_team_update || '';

const stage_code = extractStageCode(cas);
const cas_confidence = 'medium';
const event_type = input.input_type || '';
const event_date = input.date || new Date().toISOString().split('T')[0];
const summary_str = `Processed ${input.input_type} - CAS: ${cas || 'N/A'}`;
const link_or_ref = input.source_filename || '';
const raw_content = (input.content || '').substring(0, 5000);

const insertDealSQL = `INSERT INTO deals (
  deal_slug, salesforce_opportunity_id, outputs_version,
  opportunity_name, account_name, stage, cas_current, cas_next, cas_confidence,
  pain_score, power_score, vision_score, value_score, change_score, control_score
) VALUES (
  ${esc(deal_slug)}, ${esc(salesforce_opportunity_id)}, 'v4',
  ${escStr(opportunity_name)}, ${escStr(account_name)}, ${escStr(stage_code)},
  ${escStr(cas)}, ${escStr('')}, ${escStr(cas_confidence)},
  ${escNum(pain_score)}, ${escNum(power_score)}, ${escNum(vision_score)},
  ${escNum(value_score)}, ${escNum(change_score)}, ${escNum(control_score)}
)
ON CONFLICT (deal_slug) DO UPDATE SET
  salesforce_opportunity_id = EXCLUDED.salesforce_opportunity_id,
  outputs_version = EXCLUDED.outputs_version,
  opportunity_name = EXCLUDED.opportunity_name,
  account_name = EXCLUDED.account_name,
  stage = EXCLUDED.stage,
  cas_current = EXCLUDED.cas_current,
  cas_confidence = EXCLUDED.cas_confidence,
  pain_score = EXCLUDED.pain_score,
  power_score = EXCLUDED.power_score,
  vision_score = EXCLUDED.vision_score,
  value_score = EXCLUDED.value_score,
  change_score = EXCLUDED.change_score,
  control_score = EXCLUDED.control_score,
  updated_at = CURRENT_TIMESTAMP
RETURNING id`;

const updateDealSQL = `UPDATE deals SET
  salesforce_opportunity_id = ${esc(salesforce_opportunity_id)},
  outputs_version = 'v4',
  opportunity_name = ${escStr(opportunity_name)},
  account_name = ${escStr(account_name)},
  stage = ${escStr(stage_code)},
  cas_current = ${escStr(cas)},
  cas_confidence = ${escStr(cas_confidence)},
  close_date = ${close_date ? escStr(close_date) : 'NULL'},
  updated_at = CURRENT_TIMESTAMP
WHERE deal_slug = ${esc(deal_slug)}
RETURNING *`;

const saveArtifactsSQL = `INSERT INTO artifacts (
  deal_id,
  salesforce_block_1_dap,
  salesforce_pain_description, salesforce_power_description,
  salesforce_vision_description, salesforce_value_description,
  salesforce_change_description, salesforce_control_description,
  approved
) VALUES (
  (SELECT id FROM deals WHERE deal_slug = ${esc(deal_slug)}),
  ${esc(salesforce_block_1_dap)},
  ${esc(pain_evidence)}, ${esc(power_evidence)},
  ${esc(vision_evidence)}, ${esc(value_evidence)},
  ${esc(change_evidence)}, ${esc(control_evidence)},
  false
)
RETURNING id`;

const saveEvidenceSQL = `INSERT INTO p2v2c2_evidence (
  deal_id, pain_score, pain_evidence, power_score, power_evidence,
  vision_score, vision_evidence, value_score, value_evidence,
  change_score, change_evidence, control_score, control_evidence, p2v2c2_total
) VALUES (
  (SELECT id FROM deals WHERE deal_slug = ${esc(deal_slug)}),
  ${escNum(pain_score)}, ${esc(pain_evidence)},
  ${escNum(power_score)}, ${esc(power_evidence)},
  ${escNum(vision_score)}, ${esc(vision_evidence)},
  ${escNum(value_score)}, ${esc(value_evidence)},
  ${escNum(change_score)}, ${esc(change_evidence)},
  ${escNum(control_score)}, ${esc(control_evidence)},
  ${escNum(p2v2c2_total)}
)
RETURNING id`;

const saveHistorySQL = `INSERT INTO deal_history (
  deal_id, event_type, event_source, event_date, summary,
  link_or_ref, raw_content, cas_at_time, p2v2c2_total_at_time
) VALUES (
  (SELECT id FROM deals WHERE deal_slug = ${esc(deal_slug)}),
  ${escStr(event_type)}, ${esc('')}, ${escStr(event_date)}::date,
  ${escStr(summary_str)}, ${esc(link_or_ref)}, ${escStr(raw_content)},
  ${escStr(cas)}, ${escNum(p2v2c2_total)}
)
RETURNING id`;

const deal = {
  deal_slug, salesforce_opportunity_id, outputs_version: 'v4',
  opportunity_name, account_name, stage: stage_code,
  cas_current: cas, cas_next: '', cas_confidence, close_date,
  pain_score, power_score, vision_score, value_score, change_score, control_score
};

return [{
  json: {
    deal,
    _contextSource: dealContext?._contextSource || 'unknown',
    insertDealSQL, updateDealSQL, saveArtifactsSQL, saveEvidenceSQL, saveHistorySQL,
    close_date, stage, cas, forecast_category,
    salesforce_block_1_dap, risks, next_step,
    pain_score, pain_evidence,
    power_score, power_evidence,
    vision_score, vision_evidence,
    value_score, value_evidence,
    change_score, change_evidence,
    control_score, control_evidence,
    p2v2c2_total,
    call_summary, highlights, action_items, p2v2c2_scoring,
    internal_actions, services_narrative, general_narrative,
    anecdotes, internal_team_update
  }
}];

// Merge original AI outputs with edits from the review form
const orig = $('Format for PostgreSQL').item.json;
const form = $input.item.json;

function esc(val) {
  if (val === null || val === undefined || val === '') return 'NULL';
  return "'" + String(val).replace(/'/g, "''") + "'";
}
function escStr(val) {
  if (val === null || val === undefined) return 'NULL';
  return "'" + String(val).replace(/'/g, "''") + "'";
}
function escNum(val) {
  const n = Number(val);
  return isNaN(n) ? '0' : String(n);
}

function edited(formVal, origVal) {
  const f = (formVal || '').trim();
  return f.length > 0 ? f : (origVal || '');
}
function editedNum(formVal, origVal) {
  const f = String(formVal || '').trim();
  const n = Number(f);
  return (!isNaN(n) && f.length > 0) ? n : origVal;
}

// Salesforce standalone
const close_date = edited(form['close_date'], orig.close_date);
const stage = edited(form['stage'], orig.stage);
const cas = edited(form['cas'], orig.cas);
const forecast_category = edited(form['forecast_category'], orig.forecast_category);
const salesforce_block_1_dap = edited(form['salesforce_block_1_dap'], orig.salesforce_block_1_dap);
const risks = edited(form['risks'], orig.risks);
const next_step = edited(form['next_step'], orig.next_step);

// P2V2C2
const pain_score = editedNum(form['pain_score'], orig.pain_score);
const pain_evidence = edited(form['pain_evidence'], orig.pain_evidence);
const power_score = editedNum(form['power_score'], orig.power_score);
const power_evidence = edited(form['power_evidence'], orig.power_evidence);
const vision_score = editedNum(form['vision_score'], orig.vision_score);
const vision_evidence = edited(form['vision_evidence'], orig.vision_evidence);
const value_score = editedNum(form['value_score'], orig.value_score);
const value_evidence = edited(form['value_evidence'], orig.value_evidence);
const change_score = editedNum(form['change_score'], orig.change_score);
const change_evidence = edited(form['change_evidence'], orig.change_evidence);
const control_score = editedNum(form['control_score'], orig.control_score);
const control_evidence = edited(form['control_evidence'], orig.control_evidence);
const p2v2c2_total = pain_score + power_score + vision_score + value_score + change_score + control_score;

// Narratives
const call_summary = edited(form['call_summary'], orig.call_summary);
const highlights = edited(form['highlights'], orig.highlights);
const action_items = edited(form['action_items'], orig.action_items);
const p2v2c2_scoring = edited(form['p2v2c2_scoring'], orig.p2v2c2_scoring);
const internal_actions = edited(form['internal_actions'], orig.internal_actions);
const services_narrative = edited(form['services_narrative'], orig.services_narrative);
const general_narrative = edited(form['general_narrative'], orig.general_narrative);
const anecdotes = edited(form['anecdotes'], orig.anecdotes);
const internal_team_update = edited(form['internal_team_update'], orig.internal_team_update);

const deal_slug = orig.deal.deal_slug;

return [{
  json: {
    deal: orig.deal,
    close_date, stage, cas, forecast_category,
    salesforce_block_1_dap, risks, next_step,
    pain_score, pain_evidence,
    power_score, power_evidence,
    vision_score, vision_evidence,
    value_score, value_evidence,
    change_score, change_evidence,
    control_score, control_evidence,
    p2v2c2_total,
    call_summary, highlights, action_items, p2v2c2_scoring,
    internal_actions, services_narrative, general_narrative,
    anecdotes, internal_team_update
  }
}];

// Initialize a new opportunity when deal doesn't exist
const input = $('Input Classifier').item.json;

const newDeal = {
  json: {
    kind: 'deal_record',
    deal_slug: input.deal_slug,
    salesforce_opportunity_id: null,
    _contextSource: 'new_opportunity',
    outputs_version: 'v2',
    content: input.content || `New deal opportunity: ${input.opportunity_name || 'unnamed'} at ${input.account_name || 'unknown account'}. Expected close: ${input.close_date || 'TBD'}`,
    input_type: input.input_type || 'deal_intake',

    updated_at: new Date().toISOString(),

    opportunity_name: '',
    account_name: '',
    cas_current: '1A: Discovery - Initial conversation',
    cas_next: '1B: Discovery - Problem validation',
    cas_confidence: 'low',

    pain_score: 0,
    power_score: 0,
    vision_score: 0,
    value_score: 0,
    change_score: 0,
    control_score: 0
  }
};

return [newDeal];

const items = $input.all();
const dealContext = items[0];
const contextSource = dealContext.json._contextSource || 'unknown';

console.log('Merge Deal Context - contextSource:', contextSource);

return [{
  json: {
    ...dealContext.json,
    _contextSource: contextSource
  }
}];
