// Merge Assessment + Artifacts for PostgreSQL
const assessment = $('Assessment Engine (Agent 1)').item.json;
const artifacts = $('Artifact Engine (Agent 2)').item.json;
const input = $('Input Classifier').item.json;
const dealContext = $('Merge Deal Context').item.json;

if (!assessment || !artifacts || !input) {
  throw new Error('Missing required data from previous nodes');
}

// Extract stage from CAS
const stage = assessment.state?.cas_current?.split(':')[0]?.trim() || '1A';

// Build deal record
const deal = {
  deal_slug: input.deal_slug,
  salesforce_opportunity_id: assessment.salesforce_opportunity_id ||
                              dealContext?.salesforce_opportunity_id ||
                              input.salesforce_opportunity_id || null,
  outputs_version: 'v2',

  // State
  opportunity_name: assessment.state?.opportunity_name || '',
  account_name: assessment.state?.account_name || '',
  stage: stage,
  cas_current: assessment.state?.cas_current || '',
  cas_next: assessment.state?.cas_next || '',
  cas_confidence: assessment.state?.cas_confidence || 'low',
  close_date: assessment.state?.close_date || null,
  amount: assessment.state?.amount || 0,

  // TCV
  base_tcv: assessment.state?.tcv_model?.base_tcv || 0,
  expansion_tcv: assessment.state?.tcv_model?.expansion_tcv || 0,
  renewal_tcv: assessment.state?.tcv_model?.renewal_tcv || 0,

  // P2V2C2
  pain_score: assessment.state?.p2v2c2?.pain || 0,
  power_score: assessment.state?.p2v2c2?.power || 0,
  vision_score: assessment.state?.p2v2c2?.vision || 0,
  value_score: assessment.state?.p2v2c2?.value || 0,
  change_score: assessment.state?.p2v2c2?.change || 0,
  control_score: assessment.state?.p2v2c2?.control || 0,

  // Diagnostics
  delta_summary: assessment.diagnostics?.delta_summary || '',
  top_3_strengths: JSON.stringify(assessment.diagnostics?.deal_health?.top_3_strengths || []),
  top_3_risks: JSON.stringify(assessment.diagnostics?.deal_health?.top_3_risks || []),
  unknowns_blocking_progress: JSON.stringify(assessment.diagnostics?.deal_health?.unknowns_blocking_progress || [])
};

// Build P2V2C2 evidence record
const evidence = {
  pain_score: assessment.state?.p2v2c2?.pain || 0,
  pain_evidence: artifacts.artifacts?.salesforce_pain_description || assessment.evidence?.pain || '',
  power_score: assessment.state?.p2v2c2?.power || 0,
  power_evidence: artifacts.artifacts?.salesforce_power_description || assessment.evidence?.power || '',
  vision_score: assessment.state?.p2v2c2?.vision || 0,
  vision_evidence: artifacts.artifacts?.salesforce_vision_description || assessment.evidence?.vision || '',
  value_score: assessment.state?.p2v2c2?.value || 0,
  value_evidence: artifacts.artifacts?.salesforce_value_description || assessment.evidence?.value || '',
  change_score: assessment.state?.p2v2c2?.change || 0,
  change_evidence: artifacts.artifacts?.salesforce_change_description || assessment.evidence?.change || '',
  control_score: assessment.state?.p2v2c2?.control || 0,
  control_evidence: artifacts.artifacts?.salesforce_control_description || assessment.evidence?.control || '',
  p2v2c2_total: assessment.state?.p2v2c2?.total || 0
};

// Build artifacts record
const artifactsRecord = {
  salesforce_block_1_dap: artifacts.artifacts?.salesforce_block_1_dap || '',
  salesforce_pain_description: evidence.pain_evidence,
  salesforce_power_description: evidence.power_evidence,
  salesforce_vision_description: evidence.vision_evidence,
  salesforce_value_description: evidence.value_evidence,
  salesforce_change_description: evidence.change_evidence,
  salesforce_control_description: evidence.control_evidence,
  client_follow_up_email: artifacts.artifacts?.client_follow_up_email || '',
  exec_recap_forwardable: artifacts.artifacts?.exec_recap_forwardable || '',
  internal_message_pricing: artifacts.artifacts?.internal_message_pricing || '',
  meeting_prep: artifacts.artifacts?.meeting_prep || '',
  assumptions: artifacts.artifacts?.assumptions || '',
  exclusions: artifacts.artifacts?.exclusions || '',
  approved: false
};

// Build history entry
const historyEntry = {
  event_type: input.input_type,
  event_source: input.source,
  event_date: input.date,
  summary: `Processed ${input.input_type} - CAS: ${assessment.state?.cas_current || 'N/A'}`,
  link_or_ref: input.source_filename || '',
  raw_content: input.content.substring(0, 5000),
  cas_at_time: assessment.state?.cas_current,
  p2v2c2_total_at_time: assessment.state?.p2v2c2?.total || 0
};

// Build action plans
const actionPlans = (artifacts.plan?.action_plan || []).map(action => ({
  action_date: action.date,
  owner: action.owner,
  action_description: action.action,
  status: action.status || 'pending'
}));

return [{
  json: {
    deal,
    evidence,
    artifacts: artifactsRecord,
    history: historyEntry,
    actionPlans,
    _contextSource: dealContext?._contextSource || 'unknown'
  }
}];

