// Classify input type and extract content
const items = $input.all();
const results = [];

for (const item of items) {
  // Check if input is from webhook (MCP) or Gmail
  const isWebhook = item.json.body !== undefined;

  let content, subject, inputType, dealSlug, salesforceOpportunityId, date, sourceFilename;

  if (isWebhook) {
    // MCP/Webhook input
    const body = item.json.body;
    content = body.content || '';
    inputType = body.input_type || 'call_transcript';
    dealSlug = body.deal_slug || `deal-${new Date().toISOString().split('T')[0]}`;
    salesforceOpportunityId = body.salesforce_opportunity_id || null;
    date = body.date || new Date().toISOString().split('T')[0];
    sourceFilename = body.source_filename || 'mcp-input';
  } else {
    // Gmail input
    subject = item.json.subject || '';
    const text = item.json.text || item.json.textPlain || '';
    content = text;

    // Determine input type
    inputType = 'email';
    if (subject.toLowerCase().includes('transcript') || text.includes('Speaker:')) {
      inputType = 'call_transcript';
    } else if (subject.toLowerCase().includes('teams')) {
      inputType = 'teams_message';
    }

    // Extract deal slug from subject or generate
    dealSlug = subject
      .toLowerCase()
      .replace(/[^a-z0-9\s-]/g, '')
      .replace(/\s+/g, '-')
      .substring(0, 50);

    if (!dealSlug) {
      dealSlug = `deal-${new Date().toISOString().split('T')[0]}`;
    }

    salesforceOpportunityId = null; // Gmail doesn't provide SF ID
    date = new Date().toISOString().split('T')[0];
    sourceFilename = subject;
  }

  results.push({
    json: {
      inputType,
      dealSlug,
      salesforceOpportunityId,
      content,
      date,
      sourceFilename,
      source: isWebhook ? 'mcp' : 'gmail'
    }
  });
}

return results;

// Validate and deduplicate Load Deal Memory results
const items = $input.all();

// Check if no results or error from Load Deal Memory
if (!items || items.length === 0) {
  console.log('No items received from Load Deal Memory');
  return [];
}

// Filter out items with null or undefined json first
const itemsWithJson = items.filter(item => item && item.json !== null && item.json !== undefined);

if (itemsWithJson.length === 0) {
  console.log('No valid items with json data');
  return [];
}

// Check if first item is an error or has no data
if (itemsWithJson.length === 1) {
  const firstItem = itemsWithJson[0];
  if (firstItem.json.error || !firstItem.json.deal_slug) {
    // No results found - return empty so If node can create new opportunity
    console.log('No existing deal found in memory');
    return [];
  }
  // Single valid result - perfect, return as-is
  return itemsWithJson;
}

// Multiple matches found - prioritize by salesforce_opportunity_id, then most recent
console.log(`WARNING: Found ${itemsWithJson.length} matching deals. Selecting most relevant.`);

// Filter out any error items and ensure deal_slug exists
const validItems = itemsWithJson.filter(item => 
  item.json && 
  !item.json.error && 
  item.json.deal_slug
);

if (validItems.length === 0) {
  console.log('No valid items after filtering');
  return [];
}

if (validItems.length === 1) {
  return validItems;
}

// Prefer entries with salesforce_opportunity_id
const withSalesforceId = validItems.filter(item => 
  item.json && 
  item.json.salesforce_opportunity_id
);

if (withSalesforceId.length > 0) {
  // Return the one with most recent date_updated
  const sorted = withSalesforceId.sort((a, b) => {
    const dateA = new Date(a.json['meta.date_updated'] || 0);
    const dateB = new Date(b.json['meta.date_updated'] || 0);
    return dateB - dateA;
  });
  return [sorted[0]];
}

// No Salesforce ID found, return most recently updated
const sorted = validItems.sort((a, b) => {
  const dateA = new Date(a.json['meta.date_updated'] || 0);
  const dateB = new Date(b.json['meta.date_updated'] || 0);
  return dateB - dateA;
});

return [sorted[0]];

