// Merge deal context from either existing deal or new opportunity
const items = $input.all();

// Should receive exactly one item from either path
if (!items || items.length === 0) {
  throw new Error('No deal context received');
}

if (items.length > 1) {
  console.log('Multiple items received, using first one');
}

const dealContext = items[0];

// Ensure we have valid json
if (!dealContext || !dealContext.json) {
  throw new Error('Invalid deal context - missing json data');
}

// Determine context source - check if salesforce_opportunity_id exists and is not null/empty
const isExisting = dealContext.json.salesforce_opportunity_id && 
                   dealContext.json.salesforce_opportunity_id !== null && 
                   dealContext.json.salesforce_opportunity_id !== '';

// Ensure we have the expected structure
return [{
  json: {
    ...dealContext.json,
    _contextSource: isExisting ? 'existing_deal' : 'new_opportunity'
  }
}];
